const {PubgAPI} = require('./');
const fs = require('fs');

const api = new PubgAPI({apikey: process.env.PUBG_APIKEY});

const nickname = 'fak3zito';

api.getProfileByNickname(nickname)
  .then((profile) => {
    const stats = profile.getStats();
    console.log(JSON.stringify(stats, null, 2));
    fs.writeFileSync(`./testing-accounts/${nickname}.json`, JSON.stringify(profile.content, null, 2));
  });
